mapper.select:
		select `cid`,`tname`,`has_children`,`has_parent`,`tid`,`content`,`level` from title_content

		  		   and cid = #{cid} 
		  		   and tname = #{tname} 
		  		   and has_children = #{hasChildren} 
		  		   and has_parent = #{hasParent} 
		  		   and tid = #{tid} 
		  		   and content = #{content} 



                order by ${sort} ${order}


                order by cid desc



			limit #{offset}, #{limit}

select count(*) from title_content

  		   and cid = #{cid} 
  		   and tname = #{tname} 
  		   and has_children = #{hasChildren} 
  		   and has_parent = #{hasParent} 
  		   and tid = #{tid} 
  		   and content = #{content}
